}
.leftcontainer .cbp-qtrotator .cbp-qtcontent, .lightcontainer .cbp-qtrotator .cbp-qtcontent {
border-top : 1px solid #c7c7c7;
}
.leftcontainer .cbp-qtrotator img, .lightcontainer .cbp-qtrotator img, .leftcontainer .caption-image img, .lightcontainer .caption-image img, .leftcontainer .st-accordion img, .lightcontainer .st-accordion img {
border : #c7c7c7 solid 3px;
}
.rightcontainer .cbp-qtrotator .cbp-qtcontent, .darkcontainer .cbp-qtrotator .cbp-qtcontent {
border-top : 1px solid #262626;
}
.rightcontainer .cbp-qtrotator img, .darkcontainer .cbp-qtrotator img {
border : #262626 solid 3px;
}
.leftcontainer .caption-image figcaption, .lightcontainer .caption-image figcaption {
background-color : #c7c7c7;
}
.rightcontainer .caption-image img, .darkcontainer .caption-image img, .rightcontainer .st-accordion img, .darkcontainer .st-accordion img {
border : #262626 solid 3px;
}
.leftcontainer .st-accordion, .lightcontainer .st-accordion {
border-top : 1px solid #c7c7c7;
}
.leftcontainer .st-accordion ul li, .lightcontainer .st-accordion ul li {
border-bottom : 1px solid #c7c7c7;
}
.leftcontainer .st-accordion ul li > a span, .lightcontainer .st-accordion ul li > a span {
color : #262626 !important ;
}
.rightcontainer .st-accordion, .darkcontainer .st-accordion {
border-top : 1px solid #262626;
}
.rightcontainer .st-accordion ul li, .darkcontainer .st-accordion ul li, #footer-widgets, .footer-widget ul li {
border-bottom : 1px solid #262626;
}
.rightcontainer .st-accordion ul li > a span, .darkcontainer .st-accordion ul li > a span {
color : #ffffff !important ;
}
footer {
background-color : #1b1b1b;
border-top : 3px solid #05A4E1;
}
.rightcontainer .ionTabs__tab, .rightcontainer .ionTabs__tab:hover, .rightcontainer .ionTabs__tab.ionTabs__tab_state_active, .rightcontainer .ionTabs__body, .darkcontainer .ionTabs__tab, .darkcontainer .ionTabs__tab:hover, .darkcontainer .ionTabs__tab.ionTabs__tab_state_active, .darkcontainer .ionTabs__body {
background : #262626;
}
.leftcontainer .ionTabs__tab, .leftcontainer .ionTabs__tab:hover, .leftcontainer .ionTabs__tab.ionTabs__tab_state_active, .leftcontainer .ionTabs__body, .lightcontainer .ionTabs__tab, .lightcontainer .ionTabs__tab:hover, .lightcontainer .ionTabs__tab.ionTabs__tab_state_active, .lightcontainer .ionTabs__body {
background : #c7c7c7;
}
.flex-title-left, .zeus-text-left {
color : #1b1b1b;
}
.countdown div span {
border-top : 1px solid #05A4E1;
}
img.wp-smiley, img.emoji {
display : inline !important ;
border : none !important ;
box-shadow : none !important ;
height : 1em !important ;
width : 1em !important ;
margin : 0 0.07em !important ;
vertical-align : -0.1em !important ;
background : none !important ;
padding : 0 !important ;
}
.dysania-filters {
list-style : none !important ;
padding-left : 0 !important ;
padding-right : 0 !important ;
padding-top : 0 !important ;
margin-left : 0 !important ;
margin-right : 0 !important ;
margin-top : 0 !important ;
display : inline-block;
width : 100%;
vertical-align : bottom;
}
.dysania-filters li {
float : left;
margin : 0 !important ;
cursor : pointer;
vertical-align : bottom;
}
.dysania-filters li a {
vertical-align : bottom;
}
.dysania-filters li a:hover {
text-decoration : none;
}
.dysania-grid {
list-style : none !important ;
position : relative;
margin : 0 !important ;
padding : 0 !important ;
display : inline-block;
vertical-align : bottom;
width : 100%;
}
.dysania-grid li {
float : left;
position : relative;
transition : all 500ms cubic-bezier(0.645,0.045,0.355,1.000);
transition-timing-function : cubic-bezier(0.645,0.045,0.355,1.000);
margin : 0 !important ;
padding : 0 !important ;
}
li.onecolumn {
width : 100%;
}
li.twocolumns {
width : 50%;
}
li.threecolumns {
width : 33.333333333333%;
}
li.fourcolumns {
width : 25%;
}
li.fivecolumns {
width : 20%;
}
.dysania-grid li a, .dysania-grid li a img {
display : block;
position : relative;
width : 100% !important ;
height : auto !important ;
border-radius : 0 !important ;
overflow : hidden;
outline : 0 !important ;
}
.dysania-grid li a {
overflow : hidden !important ;
opacity : 1;
}
.dysania-grid li a.dysania-photo, .dysania-grid li a.dysania-iframe {
overflow : hidden;
background-position : center center;
background-repeat : no-repeat;
}
.dysania-grid li a.dysania-iframe {
overflow : hidden;
background-position : center center;
background-repeat : no-repeat;
}
.dysania-grid li a.dysania-link {
overflow : hidden;
background-position : center center;
background-repeat : no-repeat;
}
.dysania-grid li a img {
transform : scale(1), rotate(0deg);
}
.no-effect {
transform : rotate(360deg);
opacity : 0.300000011920928955078125;
}
.clear {
clear : both;
}
@media screen and (max-width:480px) {
.dysania-grid li, li.twocolumns, li.threecolumns, li.fourcolumns, li.fivecolumns {
width : 100% !important ;
}
}
#colorbox, #cboxOverlay, #cboxWrapper {
position : absolute;
top : 0;
left : 0;
z-index : 999999999;
overflow : hidden;
}
#cboxOverlay {
position : fixed;
width : 100%;
height : 100%;
}
#cboxMiddleLeft, #cboxBottomLeft {
clear : left;
}
#cboxContent {
position : relative;
}
#cboxLoadedContent {
overflow : auto;
}
#cboxTitle {
margin : 0;
}
#cboxLoadingOverlay, #cboxLoadingGraphic {
position : absolute;
top : 0;
left : 0;
width : 100%;
height : 100%;
}
#cboxPrevious, #cboxNext, #cboxClose, #cboxSlideshow {
cursor : pointer;
}
.cboxPhoto {
float : left;
margin : auto;
border : 0;
display : block;
max-width : none;
}
.cboxIframe {
width : 100%;
height : 100%;
display : block;
border : 0;
}
#colorbox, #cboxContent, #cboxLoadedContent {
box-sizing : content-box;
}
#cboxOverlay {
background : #262626;
}
#colorbox {
outline : 0;
}
#cboxContent {
margin-top : 32px;
overflow : visible;
background : #313131;
}
.cboxIframe {
background : #1b1b1b;
}
#cboxError {
padding : 50px;
border : #ccc solid 1px;
}
#cboxLoadedContent {
background : #fff;
padding : 15px;
padding-bottom : 35px;
}
#cboxLoadingGraphic {
background : url(images/loader.gif) center center no-repeat;
}
#cboxLoadingOverlay {
background : #fff;
}
#cboxTitle {
position : absolute;
bottom : 9px;
right : 15px;
color : #da2f10;
}
#cboxCurrent {
position : absolute;
top : -22px;
right : 205px;
text-indent : -9999px;
}
#cboxPrevious, #cboxNext, #cboxSlideshow, #cboxClose {
border : 0;
padding : 0;
margin : 0;
overflow : visible;
text-indent : -9999px;
width : 20px;
height : 28px;
position : absolute;
bottom : 0;
background : url(images/controls.png) 0 0 no-repeat;
outline : none;
}
#cboxPrevious:active, #cboxNext:active, #cboxSlideshow:active, #cboxClose:active {
outline : 0;
}
#cboxPrevious {
background-position : 0 0;
left : 34px;
}
#cboxPrevious:hover {
background-position : 0 -25px;
}
#cboxNext {
background-position : -25px 0;
left : 56px;
}
#cboxNext:hover {
background-position : -25px -25px;
}
#cboxClose {
background-position : -50px 0;
left : 12px;
}
#cboxClose:hover {
background-position : -50px -25px;
}
.cboxSlideshow_on #cboxPrevious, .cboxSlideshow_off #cboxPrevious {
left : 78px;
}
.cboxSlideshow_on #cboxSlideshow {
background-position : -75px -25px;
left : 56px;
}
.cboxSlideshow_on #cboxSlideshow:hover {
background-position : -100px -25px;
}
.cboxSlideshow_off #cboxSlideshow {
background-position : -100px 0;
left : 56px;
}
.cboxSlideshow_off #cboxSlideshow:hover {
background-position : -75px -25px;
}
.message {
background-size : 40px 40px;
background-image : linear-gradient(135deg, rgba(255, 255, 255, 0.05) 25%, transparent 25%, transparent 50%, rgba(255, 255, 255, 0.05) 50%, rgba(255, 255, 255, 0.05) 75%, transparent 75%, transparent);
width : 100%;
padding : 23px 20px 18px 20px;
margin-top : 0 !important ;
margin-bottom : 30px;
animation : animate-bg 5s linear infinite;
position : relative;
}
.message-close {
position : absolute;
width : 25px;
height : 25px;
right : 0;
top : 0;
cursor : pointer;
background-image : url('images/close-s.png');
background-repeat : no-repeat;
background-position : center center;
transition : opacity 0.2s ease-in-out;
}
.message-close:hover {
opacity : 0.699999988079071044921875;
}
.message, .message p, .message h1, .message h2, .message h3, .message h4, .message h5, .message h6 {
color : #fff !important ;
}
.info {
background-color : #262626;
border-bottom-color : #060606;
}
.error {
background-color : #da2f10;
border-bottom-color : #bc290e;
}
.warning {
background-color : #f1c40f;
border-bottom-color : #dbb20d;
}
.success {
background-color : #2ecc71;
border-bottom-color : #27ac5f;
}
@keyframes animate-bg {
from {
background-position : 0 0;
}
to {
background-position : -80px 0;
}
}
.flickr-box {
margin : 0;
margin-right : -5px;
padding : 0;
overflow : hidden;
width : 110%;
}
.flickr-box li {
list-style : none;
float : left;
margin-right : 5px;
margin-bottom : 5px;
padding : 0 !important ;
height : 80px !important ;
background-image : none !important ;
}
.flickr-box li:last-child {
margin-right : 0;
}
.flickr-box li {
border : none !important ;
}
.flickr-box li img {
display : block;
transition : all 0.4s ease-in-out;
border : transparent solid 5px;
width : 87px;
border : #262626 solid 3px;
opacity : 0.5;
}
.flickr-box li img:hover {
border : #fff solid 3px;
opacity : 1;
}
.sidebarbox .flickr-box li img {
width : 77px;
}
.sidebarbox .flickr-box li {
height : 72px !important ;
}
.st-accordion {
width : 100%;
margin : 0;
position : relative;
}
.st-accordion ul {
margin : 0;
padding : 0;
margin-bottom : 20px;
}
.st-accordion ul li {
height : 70px;
overflow : hidden;
}
.st-accordion ul li:first-child {
border-top : none;
}
.st-accordion ul li > a {
display : block;
position : relative;
line-height : 70px;
outline : none;
transition : color 0.2s ease-in-out;
font-size : 18px;
}
.st-accordion ul li > a .st-arrow {
background : url(images/down-arrow.png) center center no-repeat transparent;
text-indent : -9000px;
width : 34px;
height : 21px;
position : absolute;
top : 46%;
right : -26px;
margin-top : -7px;
opacity : 0;
transition : all 0.2s ease-in-out;
}
.st-accordion ul li > a:hover {
text-decoration : none;
}
.st-accordion ul li > a:hover .st-arrow {
opacity : 1;
right : 10px;
}
.st-accordion ul li.st-open > a span {
transform : rotate(180deg);
right : 10px;
opacity : 1;
}
.st-content {
padding : 0 0 30px 0;
}
.st-content img {
width : 30%;
float : left;
height : auto;
margin-right : 20px;
vertical-align : bottom;
}
.cbp-qtrotator {
position : relative;
margin : 0;
width : 100%;
}
.cbp-qtrotator .cbp-qtcontent {
position : absolute;
padding-top : 29px;
top : 0;
z-index : 0;
opacity : 0;
width : 100%;
}
.cbp-qtrotator img {
max-width : 120px;
height : auto;
float : right;
margin-left : 25px;
}
.no-js .cbp-qtrotator .cbp-qtcontent {
border-bottom : none;
}
.cbp-qtrotator .cbp-qtcontent.cbp-qtcurrent, .no-js .cbp-qtrotator .cbp-qtcontent {
position : relative;
z-index : 100;
opacity : 1;
}
.cbp-qtrotator .cbp-qtcontent:before, .cbp-qtrotator .cbp-qtcontent:after {
content : " ";
display : table;
}
.cbp-qtrotator .cbp-qtcontent:after {
clear : both;
}
.cbp-qtprogress {
position : absolute;
height : 1px;
width : 0%;
top : 0;
z-index : 1000;
}
.cbp-qtrotator .cbp-blockquote {
margin : 0;
padding : 0;
}
.cbp-qtrotator .cbp-blockquote .cbp-footer {
font-size : 14px;
}
.cbp-qtrotator .cbp-blockquote .cbp-footer:before {
content : '― ';
}
.numericlist {
counter-reset : number-counter;
margin : 0;
padding : 0;
}
.numericlist dt {
position : relative;
}
.numericlist dt:before {
content : counter(number-counter);
