}
.caption-image {
position : relative;
margin-bottom : 20px;
}
.caption-image img {
width : 100%;
height : auto;
}
.caption-image figcaption {
position : absolute;
bottom : 0;
left : 0;
padding : 10px;
width : 100%;
text-align : center;
}
.iconcontainer {
position : relative;
width : 100%;
}
.iconcontainer:after {
content : '';
display : block;
clear : both;
}
.icon {
width : 25%;
float : left;
text-align : center;
display : inline-block;
margin : 0;
position : relative;
}
.leftcontainer .icon, .rightcontainer .icon {
