border-radius : 100%;
cursor : pointer;
}
.icon h5 {
margin-top : 15px !important ;
margin-bottom : 10px !important ;
border : none !important ;
padding : 0 !important ;
}
.icon p {
text-align : center;
margin-top : 0;
margin-bottom : 30px !important ;
padding-right : 10px;
padding-left : 10px;
}
.accordion-header {
margin : 0;
padding : 10px 0 10px 0;
cursor : pointer;
text-align : left;
font-size : 18px;
transition : background-color 0.4s ease-in-out, padding 0.8s linear, color 0.4s ease-in-out;
}
.accordion-header:hover {
background : url(images/plus.png);
background-repeat : no-repeat;
background-position : right 50%;
}
.active-header {
font-size : 22px;
padding-left : 20px;
margin-top : 10px;
}
.active-header:hover {
background-image : none;
}
.accordion-content {
display : none;
padding : 20px;
width : 100%;
margin-bottom : 10px;
}
.flex-video {
position : relative;
padding-bottom : 67.5%;
height : 0;
overflow : hidden;
margin-bottom : 20px;
}
.flex-video.widescreen {
padding-bottom : 37.25%;
}
.flex-video.vimeo {
padding-top : 0;
}
.flex-video iframe, .flex-video object, .flex-video embed, .flex-video video {
